mapper.select:
select c.*, e.name masterName from clazz c left join emp e on c.master_id=e.id
c.name like concat('%', #{name}, '%') and end_date between #{begin} and #{end}
order by begin_date desc

mapper.update:
update clazz
name=#{name}, room=#{room}, begin_date=#{beginDate}, end_date=#{endDate}, master_id=#{masterId}, subject=#{subject}, update_time=NOW()
where id=#{id}
